```cpp
#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    string filename, searchWord;
    cout << "Enter the filename: ";
    cin >> filename;
    cout << "Enter the word to search for: ";
    cin >> searchWord;

    ifstream file(filename);
    if (!file.is_open()) {
        cout << "Error: Unable to open file" << endl;
        return 1;
    }

    string line;
    int wordCount = 0;
    while (getline(file, line)) {
        size_t pos = 0;
        while ((pos = line.find(searchWord, pos)) != string::npos) {
            pos += searchWord.length();
            wordCount++;
        }
    }

    cout << "The word '" << searchWord << "' occurs " << wordCount << " times in the file." << endl;

    file.close();
    return 0;
}
```

